import { useState } from 'react'

const Sermons = () => {
  const [url, setUrl] = useState('')
  const [embedUrl, setEmbedUrl] = useState('')

  const handleChange = (e) => setUrl(e.target.value)

  const handleEmbed = () => {
    // Extract video ID and create embed URL
    const match = url.match(/(?:youtu\.be\/|youtube\.com\/(?:watch\?v=|embed\/|v\/))([\w-]{11})/)
    if (match) {
      setEmbedUrl(`https://www.youtube.com/embed/${match[1]}`)
    } else {
      setEmbedUrl('')
    }
  }

  return (
    <section className='section sermons' id='sermons'>
      <h2 className='section__title'>설교</h2>
      <input
        type="text"
        placeholder="YouTube URL을 붙여넣으세요"
        value={url}
        onChange={handleChange}
        style={{ width: '90%', marginBottom: '1em' }}
      />
      <button onClick={handleEmbed} style={{ marginBottom: '1em' }}>영상 불러오기</button>
      {embedUrl && (
        <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0, width: '100%' }}>
          <iframe
            src={embedUrl}
            title="YouTube Sermon"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
          />
        </div>
      )}
    </section>
  )
}

export default Sermons
